function beep() {
  var snd = new Audio(
    "data:audio/wav;base64,"
  );
  snd.play();
}

function delete_cookie(name) {
  document.cookie = name + "=;expires=Thu, 01 Jan 1970 00:00:01 GMT;";
}

function camel2snake(s) {
  let rets = "";
  rets += s[0].toLowerCase();
  for (let i = 1; i < s.length; i += 1) {
    if (s[i] === s[i].toUpperCase()) {
      rets += "_";
      rets += s[i].toLowerCase();
    } else {
      rets += s[i];
    }
  }
  return rets;
}

function hsl2rgb(hsl) {
  let hue_s_start = hsl.indexOf("(") + 1;
  let hue_s_end = hsl.indexOf(",", hue_s_start);
  let saturation_s_start = hue_s_end + 2;
  let saturation_s_end = hsl.indexOf("%");
  let lightness_s_start = saturation_s_end + 3;
  let lightness_s_end = hsl.indexOf("%", lightness_s_start);
  let hue_s = hsl.substring(hue_s_start, hue_s_end);
  let saturation_s = hsl.substring(saturation_s_start, saturation_s_end);
  let lightness_s = hsl.substring(lightness_s_start, lightness_s_end);
  let H = Number(hue_s);
  let S = Number(saturation_s) / 100;
  let L = Number(lightness_s) / 100;
  let C = (1 - Math.abs(2 * L - 1)) * S;
  let Hp = H / 60;
  let X = C * (1 - Math.abs((Hp % 2) - 1));
  let R1 = 0;
  let G1 = 0;
  let B1 = 0;
  if (X == undefined) {
    R1 = 0;
    G1 = 0;
    B1 = 0;
  } else if (H <= 60) {
    R1 = C;
    G1 = X;
    B1 = 0;
  } else if (H <= 120) {
    R1 = X;
    G1 = C;
    B1 = 0;
  } else if (H <= 180) {
    R1 = 0;
    G1 = C;
    B1 = X;
  } else if (H <= 240) {
    R1 = 0;
    G1 = X;
    B1 = C;
  } else if (H <= 300) {
    R1 = X;
    G1 = 0;
    B1 = C;
  } else if (H <= 360) {
    R1 = C;
    G1 = 0;
    B1 = X;
  }
  let m = L - C / 2;
  let R = Math.round((R1 + m) * 255).toString();
  let G = Math.round((G1 + m) * 255).toString();
  let B = Math.round((B1 + m) * 255).toString();
  let ret = "rgb(" + R + "," + G + "," + B + ")";
  return ret;
}

export { delete_cookie, camel2snake, hsl2rgb };
